mapper.select:
SELECT ti.tid, ti.title, bi.name AS board_name, zi.name AS zone_name, DATE_FORMAT(ti.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, ti.is_top, ti.is_digest, ti.view_count, ui.login_name, ui.status AS user_status FROM thread_info ti, board_info bi, zone_info zi, user_info ui WHERE ti.fid = bi.fid AND bi.gid = zi.gid AND ti.create_user_id = ui.id
AND zi.gid = #{gid}
AND ti.fid = #{fid}
AND ti.status = #{status}
AND ti.title LIKE CONCAT('%', #{keyword}, '%')
ORDER BY ti.create_time DESC

SELECT ti.tid, ti.subject_id, ti.title, ti.create_user_id, ti.is_digest, DATE_FORMAT(ti.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, ti.view_count, ui.login_name, upi.avatar FROM thread_info ti, user_info ui, user_profile_info upi WHERE ti.create_user_id = ui.id AND ui.id = upi.user_id AND ti.fid = #{fid} AND ti.status = '1' AND ui.status = '1' AND IFNULL(ti.is_top, '') != '1'
AND ti.subject_id = #{subjectId}
ORDER BY ${defaultOrderField} DESC

SELECT ti.tid, ti.subject_id, ti.title, ti.create_user_id, ti.is_digest, DATE_FORMAT(ti.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, ti.view_count, ui.login_name, upi.avatar FROM thread_info ti, thread_reply_info tri, user_info ui, user_profile_info upi WHERE tri.tid = ti.tid AND ti.status = '1' AND ui.status = '1' AND ti.create_user_id = ui.id AND ui.id = upi.user_id AND IFNULL(ti.is_top, '') != '1' AND ti.fid = #{fid}
AND ti.subject_id = #{subjectId}
GROUP BY ti.tid, ti.title, ti.create_user_id, ti.create_time, ti.view_count ORDER BY MAX(tri.reply_time) DESC

SELECT ti.tid, ti.subject_id, ti.title, ti.create_user_id, ti.is_digest, DATE_FORMAT(ti.create_time, '%Y-%m-%d %H:%i:%s') AS create_time, ti.view_count, ui.login_name, upi.avatar FROM thread_info ti, thread_reply_info tri, user_info ui, user_profile_info upi WHERE tri.tid = ti.tid AND ti.status = '1' AND ui.status = '1' AND ti.create_user_id = ui.id AND ui.id = upi.user_id AND IFNULL(ti.is_top, '') != '1' AND ti.fid = #{fid}
AND ti.subject_id = #{subjectId}
GROUP BY ti.tid, ti.title, ti.create_user_id, ti.create_time, ti.view_count ORDER BY COUNT(tri.tid) DESC

SELECT ti.tid, ti.title, ti.create_user_id, DATE_FORMAT(ti.create_time, '%Y-%m-%d') AS create_time, ti.view_count, ui.login_name, upi.avatar FROM thread_info ti, thread_reply_info tri, user_info ui, user_profile_info upi WHERE tri.tid = ti.tid AND ti.status = '1' AND ui.status = '1' AND ti.create_user_id = ui.id AND ui.id = upi.user_id
AND ti.fid IN
#{item}
GROUP BY ti.tid, ti.title, ti.create_user_id, ti.create_time, ti.view_count ORDER BY MAX(tri.reply_time) DESC LIMIT ${num}

SELECT ti.tid, ti.title, ti.create_user_id, DATE_FORMAT(ti.create_time, '%Y-%m-%d') AS create_time, ti.view_count, ui.login_name, upi.avatar FROM thread_info ti, thread_reply_info tri, user_info ui, user_profile_info upi WHERE tri.tid = ti.tid AND ti.status = '1' AND ui.status = '1' AND ti.create_user_id = ui.id AND ui.id = upi.user_id
AND DATE_SUB(CURDATE(), INTERVAL ${cycle} DAY) <= date(tri.reply_time)
AND ti.fid IN
#{item}
GROUP BY ti.tid, ti.title, ti.create_user_id, ti.create_time, ti.view_count ORDER BY COUNT(tri.tid) DESC LIMIT ${num}

mapper.update:
UPDATE thread_info
fid=#{fid}, subject_id=#{subjectId}, title=#{title}, status=#{status}, view_count=#{viewCount}, view_count_day=#{viewCountDay}, view_count_week=#{viewCountWeek}, view_count_month=#{viewCountMonth}, is_top=#{isTop}, is_digest=#{isDigest}, last_edit_user_id=#{lastEditUserId}, last_edit_time=#{lastEditTime},
WHERE tid = #{tid}
